    private void btnEnviarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnEnviarActionPerformed
        setTextArray();
        boolean permissaoHorizontal=true, permissaoQuadrado=true, permissaoVertical=true;
        boolean quadradoVerificado=false, completo=false, mudarNivel=false;
        int guardaLinha=0, guardaColuna=0, cont=0, tabuleiroValido=0;
        System.out.println("Total: " + total);
        while(!completo){
            for (int linha = 0; linha < casa.length; linha++) {
                for (int num = 1; num <= casa.length; num++) {
                    for (int coluna = 0; coluna < casa.length; coluna++) {
                        if (casa[linha][coluna].equals("")) {
                            //Verifica linha horizontal
                            for (int antes = 0; antes < coluna; antes++) {
                                if (casa[linha][antes].equals(Integer.toString(num))) {
                                    permissaoHorizontal = false;
                                    break;
                                }
                                permissaoHorizontal=true;
                            }
                            if (permissaoHorizontal) {
                                for (int depois = coluna + 1; depois < casa.length; depois++) {
                                    if (casa[linha][depois].equals(Integer.toString(num))) {
                                        permissaoHorizontal = false;
                                        break;
                                    }
                                    permissaoHorizontal=true;
                                }
                            }

                            //Verifica linha vertical
                            for (int cima = 0; cima < linha; cima++) {
                                if (casa[cima][coluna].equals(Integer.toString(num))) {
                                    permissaoVertical = false;
                                    break;
                                }
                                permissaoVertical = true;
                            }
                            if (permissaoVertical) {
                                for (int baixo = linha + 1; baixo < casa.length; baixo++) {
                                    if (casa[baixo][coluna].equals(Integer.toString(num))) {
                                        permissaoVertical = false;
                                        break;
                                    }
                                    permissaoVertical = true;
                                }
                            }

                            //Verifica quadrado
                            for (int i = 2; i < 9; i += 3) {
                                for (int j = 2; j < 9; j += 3) {
                                    if (linha <= i && coluna <= j) {
                                        for (int linhaQ = i - 2; linhaQ <= i; linhaQ++) {
                                            for (int colunaQ = j - 2; colunaQ <= j; colunaQ++) {
                                                if (casa[linhaQ][colunaQ].equals(Integer.toString(num))) {
                                                    permissaoQuadrado = false;
                                                    break;
                                                }
                                            }
                                            if (permissaoQuadrado==false) {
                                                break;
                                            }
                                        }
                                        quadradoVerificado=true;
                                        break;
                                    }
                                }
                                if (quadradoVerificado) {
                                    break;
                                }
                            }
                            //System.out.println("H: " + permissaoHorizontal);
                            //System.out.println("V: " + permissaoVertical);
                            //System.out.println("Q: " + permissaoQuadrado+"\n");
                            if (permissaoHorizontal && permissaoVertical && permissaoQuadrado) {
                                cont++;
                                if (cont == 1) {
                                    guardaLinha = linha;
                                    guardaColuna = coluna;
                                } else if(mudarNivel==false){
                                    guardaLinha = 0;
                                    guardaColuna = 0;
                                } else {

                                }
                            }
                            permissaoHorizontal=true;
                            permissaoVertical=true;
                            permissaoQuadrado=true;
                            quadradoVerificado=false;
                        }
                    }
                    if (cont == 1) {
                        casa[guardaLinha][guardaColuna] = Integer.toString(num);
                        tabuleiroValido++;
                    }
                    cont = 0;
                }

            }
            completo=true;
            for (String[] valor : casa) {
                for (int verificaColuna = 0; verificaColuna < casa.length; verificaColuna++) {
                    if (valor[verificaColuna].equals("")) {
                        completo=false;
                        break;
                    }
                }
                if (completo==false) {
                    break;
                }
            }
            if(!completo && tabuleiroValido==0){
                mudarNivel=true;
            }
            if(tabuleiroValido==0){
//                completo=true;
                tabuleiroValido=-1;
                break;
            }
            tabuleiroValido=0;
        }
        if(tabuleiroValido==-1) {
            JOptionPane.showMessageDialog(null, "N√£o foi poss√≠vel resolver!");
        }
        setTextTabela();
        total=0;
    }//GEN-LAST:event_btnEnviarActionPerformed

